import { Component, OnInit } from '@angular/core';
import { OrderService } from 'src/app/order.service';
@Component({
  selector: 'app-manage-orders',
  templateUrl: './manage-orders.component.html',
  styleUrls: ['./manage-orders.component.css']
})
export class ManageOrdersComponent implements OnInit {
  orders: any[] = [];

  constructor(private orderService: OrderService) {}

  ngOnInit() {
    this.loadOrders();
  }

  loadOrders() {
    this.orderService.getOrders().subscribe(data => {
      this.orders = data;
    });
  }
 
  updateStatus(orderId: number, newStatus: string) {
    this.orderService.updateOrderStatus(orderId, newStatus).subscribe(
      response => {
        console.log('Status updated successfully', response);
        this.loadOrders(); // Reload orders to reflect the updated status
        alert('Order status updated successfully!'); // Notify user of success
      },
      error => {
        console.error('Error updating order status:', error);
        alert('Error updating order status: ' + error.message); // Notify user of error
      }
    );
  }
  

  deleteOrder(orderId: number) {
    this.orderService.deleteOrder(orderId).subscribe(response => {
      this.loadOrders();
    });
  }
}
<h2>Manage Orders</h2>

<table border="3">
    <thead>
        <tr>
            <th>Order Number</th>
            <th>Pickup Location</th>
            <th>Drop-Off Location</th>
            <th>Package Details</th>
            <th>Delivery Time</th>
            <th>User ID</th>
            <th>Courier ID</th>
            <th>Status</th>
            <th>Actions</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let order of orders">
            <td>{{ order.orderNumber }}</td>
            <td>{{ order.pickupLocation }}</td>
            <td>{{ order.dropOffLocation }}</td>
            <td>{{ order.packageDetails }}</td>
            <td>{{ order.deliveryTime }}</td>
            <td>{{ order.user_id }}</td>
            <td>{{ order.courier_id }}</td>
            <td>
                <select [(ngModel)]="order.status">
                    <option value="pending">Pending</option>
                    <option value="picked up">Picked Up</option>
                    <option value="in transit">In Transit</option>
                    <option value="delivered">Delivered</option>
                </select>
            </td>
            <td>
                <button (click)="updateStatus(order.orderNumber, order.status)">Update Status</button>
                <button (click)="deleteOrder(order.orderNumber)">Delete</button>
            </td>
        </tr>
    </tbody>
</table>
