import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/auth.service';
import { OrderService } from 'src/app/order.service';
import { jwtDecode } from "jwt-decode";
@Component({
  selector: 'app-manage-orders',
  templateUrl: './manage-orders.component.html',
  styleUrls: ['./manage-orders.component.css']
})
export class ManageOrdersComponent implements OnInit {
  orders: any[] = [];
  orderNumber: number = 0; // Initialize with a default number
  courierId: number = 0; // Initialize with a default number
  role:string=" ";
  constructor(private orderService: OrderService, private authService: AuthService) {
    const token = this.authService.getToken();
    if (token) {
      const decodedToken: any = jwtDecode(token);
      this.role = decodedToken.role; 
      console.log("test1:"+this.role)
    }
    else{ 
      console.log("test2:"+this.role)
    }

  }

 isAdmin(){
  if(this.role==="admin"){
    return true;
  }
  else{
    return false;
  }
 }
 

  ngOnInit() {
    this.loadOrders();
  }

  loadOrders() {
    this.orderService.getOrders().subscribe(data => {
      this.orders = data;
    });
  }
 
  updateStatus(orderId: number, newStatus: string) {
    this.orderService.updateOrderStatus(orderId, newStatus).subscribe({
      next: response => {
        console.log('Status updated successfully', response);
        this.loadOrders(); // Reload orders to reflect the updated status
         // Notify user of success
      },
      error: error => {
        console.error('Error updating order status:', error);
        alert('Error updating order status: ' + error.message); // Notify user of error
      }
    });
  }

  assignCourier(orderId: number, courierId: number) {
    this.orderService.assignCourierToOrder(orderId, courierId).subscribe({
      next: response => {
        console.log('Courier assigned successfully', response);
        this.loadOrders(); // Reload orders to reflect the assigned courier
         // Notify user of success
      },
      error: error => {
        console.error('Error assigning courier:', error);
        alert('Error assigning courier: ' + error.message); // Notify user of error
      }
    });
  }
  
  

  deleteOrder(orderId: number) {
    this.orderService.deleteOrder(orderId).subscribe(response => {
      this.loadOrders();
    });
  }
}
<div *ngIf="!isAdmin()">
    <h2>You need Administrative access!</h2>
</div>
<div *ngIf="isAdmin()" class="admin-container">
    <h2 class="manage-orders-heading">Manage Orders</h2>
    <a href="/home" class="return-button">
        <button>Return</button>
    </a>

    <table *ngIf="orders.length > 0">
        <thead>
            <tr>
                <th>Order Number</th>
                <th>Pickup Location</th>
                <th>Drop-Off Location</th>
                <th>Package Details</th>
                <th>Delivery Time</th>
                <th>User ID</th>
                <th>Courier ID</th>
                <th>Status</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let order of orders">
                <td>{{ order.orderNumber }}</td>
                <td>{{ order.pickupLocation }}</td>
                <td>{{ order.dropOffLocation }}</td>
                <td>{{ order.packageDetails }}</td>
                <td>{{ order.deliveryTime }}</td>
                <td>{{ order.user_id }}</td>
                <td>{{ order.courier_id ? order.courier_id : 'Not Assigned' }}</td>
                <td>
                    <select [(ngModel)]="order.status">
                        <option value="pending">Pending</option>
                        <option value="picked up">Picked Up</option>
                        <option value="in transit">In Transit</option>
                        <option value="delivered">Delivered</option>
                    </select>
                </td>
                <td>
                    <div class="action-buttons">
                        <button class="update-button" (click)="updateStatus(order.orderNumber, order.status)">Update Status</button>
                        <button class="delete-button" (click)="deleteOrder(order.orderNumber)">Delete</button>
                    </div>
                </td>
            </tr>
        </tbody>
    </table>

    <!-- Form for Assigning Courier -->
    <h3 class="assign-courier-form">Assign Courier to Order</h3>
    <form (ngSubmit)="assignCourier(orderNumber, courierId)" class="assign-courier-form">
        <label for="orderNumber">Order Number</label>
        <input type="number" id="orderNumber" [(ngModel)]="orderNumber" name="orderNumber" required>

        <label for="courierId">Courier ID</label>
        <input type="number" id="courierId" [(ngModel)]="courierId" name="courierId" required>

        <button type="submit" class="assign-button">Assign Courier</button>
    </form>
</div>
