import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { OrderService } from '../order.service';  // Adjust the path based on your project structure

@Component({
  selector: 'app-order-details',
  templateUrl: './order-details.component.html',
  styleUrls: ['./order-details.component.css']
})
export class OrderDetailsComponent implements OnInit {
  //orderNumber: number = 0;
  orderDetails: any = {};  // Store order details
  error: string = '';  // Store error message if any

  constructor(private route: ActivatedRoute, private orderService: OrderService) {}

  ngOnInit(): void {
    // Retrieve the orderNumber from the URL using snapshot
    const orderNumberStr = this.route.snapshot.paramMap.get('orderNumber');

    
    if (orderNumberStr) {
      const orderNumber = +orderNumberStr;  
      if (orderNumber) {
        
        this.orderService.getOrderDetails(orderNumber).subscribe({
          next: (data) => {
            this.orderDetails = data;  // Set the order details
          },
          error: (err) => {
            this.error = 'Error fetching order details.';  // Error handling
          }
        });
      } else {
        this.error = 'Invalid order number.';  // If the orderNumber is invalid
      }
    } else {
      this.error = 'Order number not provided.';  // If no order number is found in URL
    }
  }
}
<h2>Order Details</h2>

<!-- Display error message if any -->
<div *ngIf="error" class="error-message">
  <p>{{ error }}</p>
</div>

<!-- Display order details if available -->
<div *ngIf="!error && orderDetails">
  <table *ngIf="orderDetails">
    <tr>
      <th>Order Number</th>
      <td>{{ orderDetails.orderNumber }}</td>
    </tr>
    <tr>
      <th>Pickup Location</th>
      <td>{{ orderDetails.pickupLocation }}</td>
    </tr>
    <tr>
      <th>Dropoff Location</th>
      <td>{{ orderDetails.dropOffLocation }}</td>
    </tr>
    <tr>
      <th>Package Details</th>
      <td>{{ orderDetails.packageDetails }}</td>
    </tr>
    <tr>
      <th>Delivery Time</th>
      <td>{{ orderDetails.deliveryTime }}</td>
    </tr>
    <tr>
      <th>Status</th>
      <td>{{ orderDetails.status }}</td>
    </tr>
    <tr>
      <th>Courier</th>
      <td>{{ orderDetails.courierId ? orderDetails.courierId : 'Not Assigned' }}</td>
    </tr>
  </table>
</div>

<!-- Button to navigate back to user orders page -->
<div class="back-button">
  <button routerLink="/user-orders">Back to User Orders</button>
</div>
