import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { OrderService } from '../order.service';
import { AuthService } from '../auth.service';
import { jwtDecode } from "jwt-decode";
import { CourierService } from '../courier.service';
import { HttpClient } from '@angular/common/http';  // Import HttpClient
@Component({
  selector: 'app-courier',
  templateUrl: './courier.component.html',
  styleUrls: ['./courier.component.css']
})
export class CourierComponent implements OnInit {
  courierId: number= 0;
  orders: any[] = [];
  errorMessage: string = '';

  constructor(private http: HttpClient, private courierService: CourierService,private orderService: OrderService, private authService: AuthService ) {
      const token = this.authService.getToken();
    if (token) {
      const decodedToken: any = jwtDecode(token);
      this.courierId = decodedToken.sub; 
      console.log("test1:"+this.courierId)
    }
    else{ 
      console.log("test2:"+this.courierId)
    }
  }

  ngOnInit(): void {
    // Get orders for the current user
    this.courierService.getCourierOrders(this.courierId).subscribe({
      next: (data) => {
        this.orders = data; // Successfully received orders
      },
      error: (error) => {
        this.errorMessage = 'There was an error retrieving orders.'; // Handle error
        console.error(error); // Log the error for debugging
      },
      complete: () => {
        console.log('Courier orders fetch complete'); // Optionally log completion
      }
    });
  }
  updateStatus(orderId: number, newStatus: string) {
    this.orderService.updateOrderStatus(orderId, newStatus).subscribe({
      next: response => {
        console.log('Status updated successfully', response);
        this.ngOnInit(); // Reload orders to reflect the updated status
        alert('Order status updated successfully!'); // Notify user of success
      },
      error: error => {
        console.error('Error updating order status:', error);
        alert('Error updating order status: ' + error.message); // Notify user of error
      }
    });
  }
  acceptOrder(orderId: number): void {
   
    const url = `http://localhost:8080/orders/${orderId}/accept`;  // API URL for accepting order

    this.http.put(url, {}, { headers }).subscribe({
      next: () => {
        alert('Order accepted successfully!');
        this.ngOnInit();  // Refresh orders to update status
      },
      error: (error) => {
        console.error('Error accepting order:', error);
        alert('Error accepting order: ' + error.message);  // Show error message
      }
    });
  }

  declineOrder(orderId: number): void {
    this.courierService.declineOrder(orderId).subscribe({
      next: () => {
        alert('Order declined successfully!');
        this.ngOnInit(); // Refresh orders to update status
      },
      error: (error) => {
        console.error('Error declining order:', error);
        alert('Error declining order: ' + error.message);
      }
    });
  }


}
<h2>My Assigned Orders</h2>

<!-- Error message if orders fail to load -->
<p *ngIf="errorMessage" class="error">{{ errorMessage }}</p>

<!-- Orders Table -->
<table border="2" *ngIf="orders.length > 0; else noOrders">
  <thead>
    <tr>
      <th>Order ID</th>
      <th>Pickup Location</th>
      <th>Dropoff Location</th>
      <th>Package Details</th>
      <th>Status</th>
      <th>Update Status</th>
      <th>Actions</th> <!-- New column for Accept/Decline buttons -->
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let order of orders">
      <td>{{ order.orderNumber }}</td>
      <td>{{ order.pickupLocation }}</td>
      <td>{{ order.dropOffLocation }}</td>
      <td>{{ order.packageDetails }}</td>
      <td>{{ order.status }}</td>
      <td>
        <!-- Dropdown to select new status -->
        <select [(ngModel)]="order.newStatus">
          <option value="pending">Pending</option>
          <option value="picked up">Picked Up</option>
          <option value="in transit">In Transit</option>
          <option value="delivered">Delivered</option>
        </select>
        <button (click)="updateStatus(order.orderNumber, order.newStatus)">Update</button>
      </td>
      <td>
        <!-- Accept and Decline buttons -->
        <button (click)="acceptOrder(order.orderNumber)" [disabled]="order.status !== 'pending'">Accept</button>
        <button (click)="declineOrder(order.orderNumber)" [disabled]="order.status !== 'pending'">Decline</button>
      </td>
    </tr>
  </tbody>
</table>

<!-- Message for no orders found -->
<ng-template #noOrders>
  <p>No orders assigned to you currently.</p>
</ng-template>
